targets.query:
// Summarize foreground usage per app
let FgSummary = 
    Session_SessionDetail
    | where $__timeFilter(Timestamp)
    | where SessionFgAppId != ""
    | summarize 
        TotalFgCount = count()
      by App = SessionFgAppId;
// Summarize CPU usage per app
let CpuSummary = 
    Process_ProcessDetail
    | where $__timeFilter(Timestamp)
    | where AppId != ""
    | summarize 
        TotalCpuMs = sum(ProcCPUTimeMs) 
      by App = AppId;
let JoinedAll = 
  FgSummary
  | join kind=fullouter CpuSummary on App
  | extend
      App         = coalesce(App, App1),  
      TotalFgCount = coalesce(todouble(TotalFgCount), 0.0),
      TotalCpuMs = coalesce(todouble(TotalCpuMs), 0.0)
  | where isnotempty(App);
let minFg  = toscalar(JoinedAll | summarize min(TotalFgCount));
let maxFg  = toscalar(JoinedAll | summarize max(TotalFgCount));
let minCpu = toscalar(JoinedAll | summarize min(TotalCpuMs));
let maxCpu = toscalar(JoinedAll | summarize max(TotalCpuMs));
let Scored =
    JoinedAll
    | extend cpu = 1 - $app_imp_usage
    | extend
        F = (TotalFgCount - minFg)  / (maxFg  - minFg),
        C = (TotalCpuMs - minCpu) / (maxCpu - minCpu)
    | extend Score = $app_imp_usage * F + cpu * C
;
let TotalScore = toscalar(Scored | summarize sum(Score));
Scored
| extend 
    PctOfTotal = Score / TotalScore
| sort by Score desc
| extend 
    rank   = row_number(),                // â† assign rank 1..n
    cumulative_percent = 100 * row_cumsum(PctOfTotal)       // â† cumulative share
| project rank, app = App, cumulative_percent
